import java.util.Scanner;
public class Day1{
    static int startValue = 50;
    static int zeroCounter = 0;
    static Scanner justin = new Scanner(System.in);
    static boolean previousIsZero = false;
    
    public static void main(String[] args) {
        
        String input = ""; //justin.nextLine();
        
        String[] combinations = input.split("((?=L)|(?=R))");

        for(String arg : combinations) {
            System.out.println(arg);
        }

        for(String arg : combinations) {
            previousIsZero = false;
            char direction = arg.charAt(0);
            int clicks = Integer.parseInt(arg.substring(1));
            System.out.print("Richtung: " + direction + " Klicks: " + clicks + " Startwert: " + startValue);
            
            if(startValue == 0){
                previousIsZero = true;
            }

            while(clicks > 100){
                clicks -= 100;
                zeroCounter++;
            }
            
            if(direction == 'R') {
                startValue -= clicks;
            } else if(direction == 'L') {
                startValue += clicks;
            }

            if(startValue == 0){
                zeroCounter++;
                previousIsZero = true;
            }
            checkRange(previousIsZero);
            System.out.println(" Endwert: " + startValue);
        }
        System.out.println("Das Ergebnis ist: " + zeroCounter);
    }

    static void checkRange(boolean pZero){
        if(startValue >= 100){
            startValue %= 100;
            if(!pZero){
                zeroCounter++;
            }
        }
        
        if(startValue < 0){
            startValue += 100;
            if(!pZero){
                zeroCounter++;
            }
        }
    }
}
